var mapPSPConf={};<br/>

//Creating the Map.
var map = new kony.ui.Map(mapBasicConf, mapLayoutConf, mapPSPConf);<br/>

map.dismissCallout = function(location) {<br/>
  //logic to handle callout location<br/>
}</pre>

